import { Button, Container } from "react-bootstrap";
import { useContext } from "react";
import { AppContext } from "./AppContextProvider";
import { FormsModal } from "./FormsAdd.jsx";
import { TableUsers } from "./Tables.jsx";
import { useRandomImage } from "../hook/useRandomImage.jsx";

export const HeaderComponent = () => {
  const { setShowModalForm, setShowUserImage } =
    useContext(AppContext);
  const { showUserImage, fetchRandomImage } = useRandomImage();

  const openModalForm = async () => {
    await fetchRandomImage(); // Call fetchRandomImage when opening modal
    setShowUserImage(showUserImage);
    console.log(showUserImage)
    setShowModalForm(true);
  };

  return (
    <>
      <Container className="d-flex flex-column justify-content-center ">
        <Button variant="primary" onClick={openModalForm}>
          Open Modal
        </Button>
        <FormsModal showUserImage={showUserImage} />
        <TableUsers />
      </Container>
    </>
  );
};
