import React, { useState } from 'react';
import GlobalStyle from "./styles/global.js";
import styled from "styled-components";
import "react-toastify/dist/ReactToastify.css";

const Container = styled.div`
  width: 100%;
  max-width: 800px;
  margin-top: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
`;

function App() {
    const [showModal, setShowModal] = useState(false);

    const openModal = () => {
      setShowModal(true);
    };
  
    const closeModal = () => {
      setShowModal(false);
    };
  return (
    <div className="App">
      <>
        <Container>
          <Button variant="primary" onClick={openModal}>
            Open Modal
          </Button>
          <FormsModal show={showModal} onHide={closeModal} />{" "}
          {/* Passando props para formsModal */}
        </Container>
        <GlobalStyle />
      </>
    </div>
  );
}

export default App;
