import React, { useState } from "react";
import Table from "react-bootstrap/Table";
import axios from "axios";
import { toast } from "react-toastify";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPenSquare, faTrash, faUser } from "@fortawesome/free-solid-svg-icons";
import FormsModal from "./components/forms.js";
function TableUsers({ users, setUsers, setOnEdit }) {
  const [showModal, setShowModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);

  const openEditModal = (user) => {
    setSelectedUser(user);
    setShowModal(true);
  };

  const closeEditModal = () => {
    setSelectedUser(null);
    setShowModal(false);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete("http://localhost:8800/" + id);
      const newArray = users.filter((user) => user.id !== id);
      setUsers(newArray);
      toast.success("User deleted successfully");
    } catch (error) {
      toast.error("Failed to delete user");
    }
    setOnEdit(null);
  };

  return (
    <div>
      <Table responsive>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Idade</th>
            <th>Data de Nascimento</th>
            <th>Estado</th>
            <th></th>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {users.map((item, i) => (
            <tr key={i}>
              <td align="center">{item.name}</td>
              <td align="center">{item.age}</td>
              <td align="center">{item.b_date}</td>
              <td align="center">{item.state}</td>
              <td align="center">
                <FontAwesomeIcon icon={faUser} />
              </td>
              <td align="center">
                <FontAwesomeIcon
                  icon={faPenSquare}
                  onClick={() => openEditModal(item)}
                />
              </td>
              <td align="center">
                <FontAwesomeIcon
                  icon={faTrash}
                  onClick={() => handleDelete(item.id)}
                  style={{ cursor: "pointer" }}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <FormsModal
        show={showModal}
        onHide={closeEditModal}
        selectedUser={selectedUser}
      />
    </div>
  );
}

export default TableUsers;
