import React, { useEffect, useRef, useState } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import { toast } from "react-toastify";
import { Axios } from "axios";

function FormsModal({ props, getUsers, onEdit, setOnEdit }) {
  const ref = useRef();
  const [age, setAge] = useState("");

  useEffect(() => {
    if (onEdit) {
      const user = ref.current;

      user.name.value = onEdit.name;
      user.b_date.value = onEdit.b_date;
      user.age.value = onEdit.age;
      user.state.value = onEdit.state;
      user.city.value = onEdit.city;
      user.neighborhood.value = onEdit.neighborhood;
      user.road.value = onEdit.road;
      user.biography.value = onEdit.biography;
    }
  }, [onEdit]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const user = ref.current;

    if (
      !user.name.value ||
      !user.b_date.value ||
      !user.age.value ||
      !user.state.value ||
      !user.city.value ||
      !user.neighborhood.value ||
      !user.road.value ||
      !user.biography.value
    ) {
      return toast.warn("Preencha todos campos!");
    }

    if (onEdit) {
      await Axios.put("http://localhost:8800/" + onEdit.id, {
        name: user.name.value,
        b_date: user.b_date.value,
        age: user.age.value,
        state: user.state.value,
        city: user.city.value,
        neighborhood: user.neighborhood.value,
        naroadme: user.road.value,
        biography: user.biography.value,
      })
        .then(({ data }) => toast.success(data))
        .catch(({ data }) => toast.error(data));
    } else {
      await Axios.put("http://localhost:8800/", {
        name: user.name.value,
        b_date: user.b_date.value,
        age: user.age.value,
        state: user.state.value,
        city: user.city.value,
        neighborhood: user.neighborhood.value,
        naroadme: user.road.value,
        biography: user.biography.value,
      })
        .then(({ data }) => toast.success(data))
        .catch(({ data }) => toast.error(data));
    }

    user.name.value = "";
    user.b_date.value = "";
    user.age.value = "";
    user.state.value = "";
    user.city.value = "";
    user.neighborhood.value = "";
    user.road.value = "";
    user.biography.value = "";

    setAge("");

    setOnEdit(null);
    getUsers();
  };

  const handleDateChange = (e) => {
    const birthDate = new Date(e.target.value);
    const today = new Date();
    let age = today.getFullYear() - birthDate.getFullYear();
    const monthDiff = today.getMonth() - birthDate.getMonth();
    if (
      monthDiff < 0 ||
      (monthDiff === 0 && today.getDate() < birthDate.getDate())
    ) {
      age--;
    }
    setAge(age.toString());
  };

  return (
    <Modal show={props.show} onHide={props.onHide}>
      <Modal.Dialog>
        <Modal.Header closeButton>
          <Modal.Title>Adicionar usuário</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formName">
              <Form.Label>Nome:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Digite o nome"
                name="name"
              />
            </Form.Group>
            <Form.Group controlId="formBDate">
              <Form.Label>Data de Nascimento:</Form.Label>
              <Form.Control
                type="date"
                name="b_date"
                onChange={handleDateChange}
              />
            </Form.Group>
            <Form.Group controlId="formAge">
              <Form.Label>Idade:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Sua idade"
                name="age"
                value={age}
                readOnly
              />
            </Form.Group>
            <Form.Group controlId="formState">
              <Form.Label>Estado:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Digite o estado"
                name="state"
              />
            </Form.Group>
            <Form.Group controlId="formCity">
              <Form.Label>Cidade:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Digite a cidade"
                name="city"
              />
            </Form.Group>
            <Form.Group controlId="formNeighborhood">
              <Form.Label>Bairro:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Digite o bairro"
                name="neighborhood"
              />
            </Form.Group>
            <Form.Group controlId="formStreet">
              <Form.Label>Rua:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Digite a rua"
                name="road"
              />
            </Form.Group>
            <Form.Group controlId="formBio">
              <Form.Label>Biografia:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Digite a sua biografia"
                name="biography"
              />
            </Form.Group>
            <Button variant="secondary" onClick={props.onHide}>
              Fechar
            </Button>
            <Button variant="primary" type="submit">
              Salvar alterações
            </Button>
          </Form>
        </Modal.Body>
        <Modal.Footer></Modal.Footer>
      </Modal.Dialog>
    </Modal>
  );
}

export default FormsModal;
