import { createContext, useContext } from "react";
export const AppContext = createContext();
export const AppContextProvider = ({ children }) => {
  const {
    showModalForm,
    setShowModalForm,
    showModalViewUser,
    setShowModalViewUser,
    setShowModalEditUser,
  } = useContext(AppContext);
  return (
    <AppContext.Provider
      value={{
        showModalForm,
        setShowModalForm,
        showModalViewUser,
        setShowModalViewUser,
        setShowModalEditUser,
      }}
    >
      {children}
    </AppContext.Provider>
  );
};
