import { useState, useEffect } from "react";
import { createUser } from "../services/api";
import { useUsers } from "./useUsers"; // Importe o hook useUsers aqui

export const useCreateUsers = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const { refetchUsers } = useUsers(); // Use o hook useUsers para acessar refetchUsers

  const createUserHandler = async (userData) => {
    setIsLoading(true);
    try {
      await createUser(userData); // Supondo que createUser retorna uma Promise
      refetchUsers(); // Chame refetchUsers após a criação bem-sucedida
    } catch (error) {
      setError(error);
    } finally {
      setIsLoading(false);
    }
  };

  return { isLoading, error, createUserHandler };
};
