import React, { useEffect, useRef, useState } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import { toast } from "react-toastify";
import axios from "axios";

function FormsModal({ props, getUsers, onEdit, setOnEdit }) {
  const userFormRef = useRef();
  const [age, setAge] = useState("");

  useEffect(() => {
    if (onEdit) {
      const userData = {
        name: onEdit.name,
        b_date: onEdit.b_date,
        age: onEdit.age,
        state: onEdit.state,
        city: onEdit.city,
        neighborhood: onEdit.neighborhood,
        road: onEdit.road,
        biography: onEdit.biography,
      };
      // Preenche o formulário com os dados do usuário em edição
      Object.keys(userData).forEach((key) => {
        userFormRef.current[key].value = userData[key];
      });
    }
  }, [onEdit]);

  const handleDateChange = (e) => {
    const birthDate = new Date(e.target.value);
    const today = new Date();
    let userAge = today.getFullYear() - birthDate.getFullYear();
    const monthDiff = today.getMonth() - birthDate.getMonth();
    if (
      monthDiff < 0 ||
      (monthDiff === 0 && today.getDate() < birthDate.getDate())
    ) {
      userAge--;
    }
    setAge(userAge.toString());
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const user = userFormRef.current;

    const userData = {
      name: user.name.value,
      b_date: user.b_date.value,
      age: user.age.value,
      state: user.state.value,
      city: user.city.value,
      neighborhood: user.neighborhood.value,
      road: user.road.value,
      biography: user.biography.value,
    };

    // Verifica se todos os campos foram preenchidos
    const requiredFields = [
      "name",
      "b_date",
      "age",
      "state",
      "city",
      "neighborhood",
      "road",
      "biography",
    ];
    for (const field of requiredFields) {
      if (!userData[field]) {
        return toast.warn("Preencha todos os campos!");
      }
    }

    try {
      if (onEdit) {
        await axios.put(`http://localhost:8800/${onEdit.id}`, userData);
      } else {
        await axios.post("http://localhost:8800", userData);
      }
      toast.success("Operação realizada com sucesso!");
      setAge(""); // Limpa o campo de idade
      getUsers();
      props.onHide();
    } catch (error) {
      toast.error("Erro ao processar a requisição!");
    }

    // Limpa os campos do formulário
    userFormRef.current.reset();
  };

  return (
    <Modal show={props.show} onHide={props.onHide}>
      <Modal.Dialog>
        <Modal.Header closeButton>
          <Modal.Title>Adicionar usuário</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form ref={userFormRef} onSubmit={handleSubmit}>
            <Form.Group controlId="formName">
              <Form.Label>Nome:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Digite o nome"
                name="name"
              />
            </Form.Group>
            <Form.Group controlId="formBDate">
              <Form.Label>Data de Nascimento:</Form.Label>
              <Form.Control
                type="date"
                name="b_date"
                onChange={handleDateChange}
              />
            </Form.Group>
            <Form.Group controlId="formAge">
              <Form.Label>Idade:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Sua idade"
                name="age"
                value={age}
                readOnly
              />
            </Form.Group>
            <Form.Group controlId="formState">
              <Form.Label>Estado:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Digite o estado"
                name="state"
              />
            </Form.Group>
            <Form.Group controlId="formCity">
              <Form.Label>Cidade:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Digite a cidade"
                name="city"
              />
            </Form.Group>
            <Form.Group controlId="formNeighborhood">
              <Form.Label>Bairro:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Digite o bairro"
                name="neighborhood"
              />
            </Form.Group>
            <Form.Group controlId="formStreet">
              <Form.Label>Rua:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Digite a rua"
                name="road"
              />
            </Form.Group>
            <Form.Group controlId="formBio">
              <Form.Label>Biografia:</Form.Label>
              <Form.Control
                type="text"
                placeholder="Digite a sua biografia"
                name="biography"
              />
            </Form.Group>
            <Button variant="secondary" onClick={props.onHide}>
              Fechar
            </Button>
            <Button variant="primary" type="submit">
              Salvar alterações
            </Button>
          </Form>
        </Modal.Body>
        <Modal.Footer></Modal.Footer>
      </Modal.Dialog>
    </Modal>
  );
}


export default FormsModal;
