import React, { useContext } from "react";
import { ModalComponent } from "./ModalComponent";
import { AppContext } from "./AppContextProvider";
import { Card } from "react-bootstrap";

export const ViewUserModal = () => {
  const {
    showModalViewUser,
    setShowModalViewUser,
    viewingUser,
    showUserImage,
  } = useContext(AppContext);

  return (
    <>
      <ModalComponent
        show={showModalViewUser}
        titleModal={"Visualizar Usuário"}
      >
        <Card>
          <Card.Body>
            <Card.Title>{viewingUser.name}</Card.Title>
            <Card.Text>
              <img
                src={showUserImage}
                alt="Imagem do Usuário"
                style={{ width: "100px", height: "100px" }}
                className="justify-content-center"
              />
              <strong>Idade:</strong> {viewingUser.age} anos
              <strong>Data de Nascimento:</strong> {viewingUser.b_date}
              <strong>Estado:</strong> {viewingUser.state}
              <strong>Estado:</strong> {viewingUser.state}
              <strong>Cidade:</strong> {viewingUser.city}
              <strong>Bairro:</strong> {viewingUser.neighborhood}
              <strong>Rua:</strong> {viewingUser.road}
              <strong>Biografia:</strong> {viewingUser.biography}
            </Card.Text>
          </Card.Body>
        </Card>
      </ModalComponent>
    </>
  );
};
