import { useState, useEffect } from "react";
import axios from "axios";
import { toast } from "react-toastify";
const API_URL = "https://source.unsplash.com/random/800x600?people";

export const useRandomImage = () => {
  const [showUserImage, setShowUserImage] = useState(null);

  const fetchRandomImage = async () => {
    try {
      const response = await axios.get(API_URL);
      console.log("Response data:", response.data);
      if (response.status === 200) {
        setShowUserImage(response.data.url);
      } else {
        toast.error("Erro ao buscar a imagem aleat贸ria.");
      }
    } catch (error) {
      console.error("Error fetching image:", error);
      toast.error("Erro ao buscar a imagem aleat贸ria.");
    }
  };

  useEffect(() => {
    fetchRandomImage();
  }, []);

  useEffect(() => {
    console.log("showUserImage:", showUserImage);
  }, [showUserImage]);

  return { showUserImage, fetchRandomImage };
};
