import React, { useContext } from "react";
import Table from "react-bootstrap/Table";
import axios from "axios";
import { toast } from "react-toastify";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPenSquare,
  faTrash,
  faUser,
} from "@fortawesome/free-solid-svg-icons";
import FormsModal from "./Forms.jsx";
import ViewUserModal from "./UserInfo.jsx";
import { AppContext } from "./AppContextProvider.jsx";
import { useUsers } from "../hook/useUsers.jsx";
import { useDeleteUsers } from "../hook/useDeleteUsers.jsx";

function TableUsers({ setUsers, setOnEdit }) {
  const {
    setShowModalViewUser,
    showModalViewUser,
    setShowModalEditUser,
    showModalEditUser,
  } = useContext(AppContext);
  const {
    users,
    isLoading: usersLoading,
    error: usersError,
    refetchUsers,
  } = useUsers();
  const {
    deleteUserById,
    isLoading: deleteLoading,
    error: deleteError,
  } = useDeleteUsers();

  const openEditModal = (item) => {
    // setOnEdit(item);
    setShowModalEditUser(true);
    console.log(showModalEditUser);
  };

  const openViewUser = (item) => {
    // setOnEdit(item);
    setShowModalViewUser(true);
  };

  const handleDelete = (id) => {
    deleteUserById(id, () => {
      refetchUsers();
    });
  };

  return (
    <div>
      <Table responsive>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Idade</th>
            <th>Data de Nascimento</th>
            <th>Estado</th>
            <th></th>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {users.map((item, i) => (
            <tr key={i}>
              <td align="center">{item.name}</td>
              <td align="center">{item.age}</td>
              <td align="center">{item.b_date}</td>
              <td align="center">{item.state}</td>
              <td align="center">
                <FontAwesomeIcon
                  icon={faUser}
                  onClick={() => openViewUser(item)}
                />
              </td>
              <td align="center">
                <FontAwesomeIcon
                  icon={faPenSquare}
                  onClick={() => openEditModal(item)}
                />
              </td>
              <td align="center">
                <FontAwesomeIcon
                  icon={faTrash}
                  onClick={() => handleDelete(item.id)}
                  style={{ cursor: "pointer" }}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <FormsModal show={showModalEditUser} />
      <ViewUserModal show={showModalViewUser} />
    </div>
  );
}

export default TableUsers;
