import React from "react";
import Table from "react-bootstrap/Table";
import axios from "axios";
import { toast } from "react-toastify";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPenSquare,
  faTrash,
  faUser,
} from "@fortawesome/free-solid-svg-icons"; // Import the icons you want to use

function TableUsers({ users, setUsers, setOnEdit }) {
  const handleEdit = (item) => {
    setOnEdit(item);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete("http://localhost:8800/" + id);
      const newArray = users.filter((user) => user.id !== id);
      setUsers(newArray);
      toast.success("User deleted successfully");
    } catch (error) {
      toast.error("Failed to delete user");
    }
    setOnEdit(null);
  };

  return (
    <Table responsive>
      <thead>
        <tr>
          <th>Nome</th>
          <th>Idade</th>
          <th>Data de Nascimento</th>
          <th>Estado</th>
          <th></th>
          <th></th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        {users.map((item, i) => (
          <tr key={i}>
            <td align="center">{item.name}</td>
            <td align="center">{item.age}</td>
            <td align="center">{item.b_date}</td>
            <td align="center">{item.state}</td>
            <td align="center">
              <FontAwesomeIcon icon={faUser} />
            </td>
            <td align="center">
              <FontAwesomeIcon icon={faPenSquare} />
            </td>
            <td align="center">
              <FontAwesomeIcon icon={faTrash} onClick={() => handleDelete(item.id)}/>
            </td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
}

export default TableUsers;
