import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
// import Modal from 'react-bootstrap/Modal';
import FormsModal from './components/forms.js';
import tableUsers from './components/tables.js';
import GlobalStyle from "./styles/global.js";
// import styled from "styled-components";
import Container from "./styles/containerStyles.js";
import "react-toastify/dist/ReactToastify.css";
// import { toast, ToastContainer } from "react-toastify";
// import axios from "axios";


function App() {
    const [showModal, setShowModal] = useState(false);

    const openModal = () => {
      setShowModal(true);
    };
  
    const closeModal = () => {
      setShowModal(false);
    };
  return (
    <div className="App">
      <>
      <Container>
          <Button variant="primary" onClick={openModal}>Open Modal</Button>
          <FormsModal show={showModal} onHide={closeModal} /> 
          <tableUsers />s
        </Container>
        <GlobalStyle />
      </>
    </div>
  );
}

export default App;
