import React, { useState, useRef, useEffect } from "react";
import { Form, Button, Modal } from "react-bootstrap";
import axios from "axios"; // Certifique-se de importar o axios
import { toast } from "react-toastify";

function FormsModal({ getUsers, onEdit, setOnEdit, show, onHide }) {
  // Adicione 'show' e 'onHide' aos props
  const ref = useRef();
  const [idade, setIdade] = useState("");
  const [formData, setFormData] = useState({
    name: "",
    b_date: "",
    age: "",
    state: "",
    city: "",
    neighborhood: "",
    road: "",
    biography: "",
  });

  useEffect(() => {
    if (onEdit) {
      const user = ref.current;
      user.nome.value = onEdit.nome;
      user.data_nascimento.value = onEdit.data_nascimento;
      user.idade.value = onEdit.idade;
      user.rua.value = onEdit.rua;
      user.bairro.value = onEdit.bairro;
      user.estado.value = onEdit.estado;
      user.biografia.value = onEdit.biografia;
    }
  }, [onEdit]);

  const handleChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleDateChange = (e) => {
    const birthDate = new Date(e.target.value);
    const today = new Date();
    let age = today.getFullYear() - birthDate.getFullYear();
    const monthDiff = today.getMonth() - birthDate.getMonth();
    if (
      monthDiff < 0 ||
      (monthDiff === 0 && today.getDate() < birthDate.getDate())
    ) {
      age--;
    }
    setIdade(age);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const user = ref.current;

    if (
      !user.nome.value ||
      !user.data_nascimento.value ||
      !user.idade.value ||
      !user.rua.value ||
      !user.bairro.value ||
      !user.estado.value ||
      !user.biografia.value
    ) {
      return toast.warn("Preencha todos os campos!");
    }

    if (onEdit) {
      await axios
        .put("http://localhost:8800/" + onEdit.id, {
          nome: user.nome.value,
          data_nascimento: user.data_nascimento.value,
          idade: user.idade.value,
          rua: user.rua.value,
          bairro: user.bairro.value,
          estado: user.estado.value,
          biografia: user.biografia.value,
        })
        .then(({ data }) => toast.success(data))
        .catch(({ data }) => toast.error(data));
    } else {
      await axios
        .post("http://localhost:8800", {
          nome: user.nome.value,
          data_nascimento: user.data_nascimento.value,
          idade: user.idade.value,
          rua: user.rua.value,
          bairro: user.bairro.value,
          estado: user.estado.value,
          biografia: user.biografia.value,
        })
        .then(({ data }) => toast.success(data))
        .catch(({ data }) => toast.error(data));
    }

    user.nome.value = "";
    user.data_nascimento.value = "";
    user.idade.value = "";
    user.rua.value = "";
    user.bairro.value = "";
    user.estado.value = "";
    user.biografia.value = "";

    setFormData({
      name: "",
      b_date: "",
      age: "",
      state: "",
      city: "",
      neighborhood: "",
      road: "",
      biography: "",
    });
    setIdade(""); // Limpa o campo de idade

    setOnEdit(null);
    getUsers();
  };

  return (
    <Modal
      show={show} // Use 'show' diretamente
      onHide={() => {
        onHide(); // Use 'onHide' diretamente
        setFormData({
          name: "",
          b_date: "",
          age: "",
          state: "",
          city: "",
          neighborhood: "",
          road: "",
          biography: "",
        });
        setIdade("");
      }}
    >
      <Modal.Header closeButton>
        <Modal.Title>Informações Pessoais</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form ref={ref} onSubmit={handleSubmit}>
          <Form.Group controlId="formName">
            <Form.Label>Nome</Form.Label>
            <Form.Control
              type="text"
              name="nome"
              placeholder="Digite seu nome"
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group controlId="formBDate">
            <Form.Label>Data de Nascimento</Form.Label>
            <Form.Control
              type="date"
              name="data_nascimento"
              placeholder="Selecione sua data de nascimento"
              onChange={handleDateChange}
            />
          </Form.Group>
          <Form.Group controlId="formAge">
            <Form.Label>Idade</Form.Label>
            <Form.Control
              type="number"
              name="idade"
              placeholder="Sua idade"
              value={idade}
              readOnly
            />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={() => onHide()}>
          {" "}
        </Button>
      </Modal.Footer>
    </Modal>
  );
}

export default FormsModal;
