import styled from "styled-components";

const TableStyle = styled.di`
.custom-table td:nth-child(3),
.custom-table td:nth-child(4) {
   display: none;
 }

 @media (min-width: 740px) {
  .custom-table td:nth-child(3),
  .custom-table td:nth-child(4) {
     display: table-cell;
   }
 }
`;

export default TableStyle;
