import { useState } from "react";
import { createUser } from "../services/api";
import { useUsers } from "./useUsers";

export const useCreateUsers = () => {
  const { refetchUsers } = useUsers(); // Importando a função refetchUsers do hook useUsers
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const createUserHandler = async (userData) => {
    setIsLoading(true);
    try {
      await createUser(userData);
      // Após criar o usuário com sucesso, atualize a lista de usuários
      refetchUsers();
    } catch (error) {
      setError(error);
    } finally {
      setIsLoading(false);
    }
  };

  return { isLoading, error, createUserHandler };
};
